let burgerBtn=document.querySelector(".header-burger"),burgerAction=document.querySelector(".header-burger-inner"),headerLink=document.querySelectorAll(".header-link"),menu=document.querySelector(".header-menu");burgerBtn.addEventListener("click",()=>{menu.classList.toggle("active"),burgerAction.classList.toggle("active")}),headerLink.forEach(e=>e.addEventListener("click",()=>{headerLink.forEach(e=>e.classList.remove("active")),e.classList.add("active")}));